USE kokebok;

INSERT INTO oppskrifter (Oppskriftstittel, Ingredienser, Allergier, Kategori, Oppskriftstekst)
VALUES ('Kålrabistappe', 'Kålrot, Gulrøtter, Fløte, Smør, Salt, Pepper', 'Laktose', 'Norsk', 'Kok kålrot og gulrøtter. Mos mens du tilsetter smør og sper med fløte. Smak til med salt og pepper.'),
       ('Gulrotsuppe', 'Gulrøtter, Currypaste, Smør, Salt, Pepper, Hvitløk, Løk, Kokosmelk', '', 'Asiatisk', 'Smelt smør og stek løk til den er blank. Ha i hvitløk og stek i 1 minutt. Ha i currypaste og stek i 2 minutter. Ha i kokosmelk og oppkuttede gulrøtter og kok i 20-30 minutter til gulrøttene er møre. Bruk stavmikser for å få en jevn suppe. Smak til med salt og pepper.'),
       ('Lynrask linsegryte', 'Hermetiske røde linser, Buljongterning, Currypaste, Olje, Salt, Pepper, Hvitløk, Løk, Kokosmelk, Hermetiske tomater', '', 'Indisk', 'Stek løk til den er blank. Ha i hvitløk og stek i 1 minutt. Ha i currypaste og stek i 2 minutter. Ha i buljong, kokosmelk og hermetiske tomater og kok opp i 5 minutter. Tilsett skylte linser. Smak til med salt og pepper.'),
       ('Bruschetta', 'Mozzarella, Cherrytomater, Olivenolje, Salt, Pepper, Hvitløk, Basilikum, Brød', 'Gluten', 'Italiensk', 'Ta skivet brød, pensle med olivenolje og stek de raskt i ovnen. Bland hakkede tomater, olivenolje, finhakket hvitløk, hakket basilikum, salt og pepper i en bolle. Fordel blandingen på brødskivene og topp med noen skiver mozzarella.');


